mapper.select:
SELECT
       menu_code
     , menu_name
     , menu_price
     , category_code
     , orderable_status
  FROM tbl_menu
 WHERE orderable_status = 'Y'


    AND menu_price < #{ price }



    AND menu_price BETWEEN 10000 AND #{ price }


    AND menu_price BETWEEN 20000 AND #{ price }


    AND menu_price BETWEEN 30000 AND #{ price }

ORDER BY menu_code
